import{_ as e,p as n,q as r,s as t,R as l,Z as a}from"./framework-47a2e622.js";const s={},d=t("h2",{id:"简介",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#简介","aria-hidden":"true"},"#"),l(" 简介")],-1),c=t("p",null,"arp-scan 是 Kali Linux 自带的一款 ARP 扫描工具。该工具可以进行单一目标扫描，也可以进行批量扫描。批量扫描的时候，用户可以通过 CIDR 地址范围或者列表文件的方式指定。该工具允许用户定制 ARP 包，构建非标准数据包。同时，该工具会自动解析 Mac 地址，给出 MAC 对应的硬件厂商，帮助用户确认目标。",-1),i=t("div",{class:"custom-container info"},[t("svg",{xmlns:"http://www.w3.org/2000/svg","xmlns:xlink":"http://www.w3.org/1999/xlink",viewBox:"0 0 24 24"},[t("g",{fill:"none",stroke:"currentColor","stroke-width":"2","stroke-linecap":"round","stroke-linejoin":"round"},[t("circle",{cx:"12",cy:"12",r:"9"}),t("path",{d:"M12 8h.01"}),t("path",{d:"M11 12h1v4h1"})])]),t("p",{class:"custom-container-title"},"什么是：ARP"),t("p",null,"地址解析协议（Address Resolution Protocol），其基本功能为透过目标设备的 IP 地址，查询目标设备的 MAC 地址，以保证通信的顺利进行。它是 IPv4 中网络层必不可少的协议，是根据 IP 地址获得物理地址 MAC 的一个 TCP/IP 协议。通过 ARP，在局域网可以很容易构成一个 ARP 欺骗，从而得到有用数据信息。")],-1),o=a('<h2 id="参数" tabindex="-1"><a class="header-anchor" href="#参数" aria-hidden="true">#</a> 参数</h2><table><thead><tr><th style="text-align:center;">参数名</th><th style="text-align:center;">参数含义</th><th style="text-align:center;">使用示例</th></tr></thead><tbody><tr><td style="text-align:center;">-f</td><td style="text-align:center;">从指定文件中读取主机名或地址</td><td style="text-align:center;">arp-scan -f ip.txt</td></tr><tr><td style="text-align:center;">-l</td><td style="text-align:center;">从网络接口配置生成地址</td><td style="text-align:center;">arp-scan -l</td></tr><tr><td style="text-align:center;">-i</td><td style="text-align:center;">各扫描之间的时间差</td><td style="text-align:center;">arp-scan -l -i 1000</td></tr><tr><td style="text-align:center;">-r</td><td style="text-align:center;">每个主机扫描次数</td><td style="text-align:center;">arp-scan -l -r 5</td></tr><tr><td style="text-align:center;">-V</td><td style="text-align:center;">显示程序版本并退出</td><td style="text-align:center;">arp-scan -l -V</td></tr><tr><td style="text-align:center;">-t</td><td style="text-align:center;">设置主机超时时间</td><td style="text-align:center;">arp-scan -t 1000 192.168.75.0/24</td></tr><tr><td style="text-align:center;">-L</td><td style="text-align:center;">使用网络接口</td><td style="text-align:center;">arp-scan -L eth0</td></tr><tr><td style="text-align:center;">-g</td><td style="text-align:center;">不显示重复的数据</td><td style="text-align:center;">arp-scan -l -g</td></tr><tr><td style="text-align:center;">-D</td><td style="text-align:center;">显示数据包往返时间</td><td style="text-align:center;">arp-scan -l -D</td></tr></tbody></table>',2),x=t("div",{class:"custom-container tip"},[t("svg",{xmlns:"http://www.w3.org/2000/svg","xmlns:xlink":"http://www.w3.org/1999/xlink",viewBox:"0 0 24 24"},[t("g",{fill:"none",stroke:"currentColor","stroke-width":"2","stroke-linecap":"round","stroke-linejoin":"round"},[t("circle",{cx:"12",cy:"12",r:"9"}),t("path",{d:"M12 8h.01"}),t("path",{d:"M11 12h1v4h1"})])]),t("p",{class:"custom-container-title"},"技巧"),t("p",null,"轻量级扫描工具，用来扫描局域网的主机还是挺好用的，由于扫描的少，所以扫描速度比较快，可以很快的查看到局域网有哪些主机和对应的 MAC 地址,配合 nmap 一起使用有奇效。")],-1),h=[d,c,i,o,x];function g(p,y){return n(),r("div",null,h)}const u=e(s,[["render",g],["__file","arpscan.html.vue"]]);export{u as default};
